import * as React from "react";
import {
  Text,
  View,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  FlatList,
  CheckBox,
} from "react-native";
import { RadioButton } from "react-native-paper";
import { Header, Icon } from "react-native-elements";

export default class CheckBoxScreen extends React.Component {
  constructor() {
    super();
    this.state = {
      isSelected1: false,
      isSelected2: false,
      isSelected3: false,
      isSelected4: false,
      totalPoints: 0,
    };
  }
  render() {
    console.log(this.state.totalPoints);
    return (
      <View>
        <Header
          leftComponent={
            <Icon
              name="arrow-left"
              type="feather"
              color="white"
              onPress={() => {
                this.props.navigation.navigate("Drawer");
              }}
            />
          }
          centerComponent={{
            text: "CheckBox",
            style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
          }}
          backgroundColor="black"
        />

        {/* Question 1 */}

        <View>
          <View style={{ display: "flex", flexDirection: "row" }}>
            <View style={{ width: "50%" }}>
              <RadioButton
                value={true}
                status={
                  this.state.isSelected1 === true ? "checked" : "unchecked"
                }
                onPress={() => {
                  var isSelected1 = this.state.isSelected1;
                  const defaultPoints = this.state.totalPoints;
                  var totalPoints = defaultPoints + 1;
                  this.setState({
                    isSelected3: !isSelected1,
                    isSelected1: false,
                    isSelected2: false,
                    isSelected4: false,
                  });

                  !this.state.isSelected1 === true
                    ? this.setState({ totalPoints: totalPoints })
                    : this.setState({ totalPoints: defaultPoints });
                }}
              />
            </View>
            <Text style={{ marginLeft: "-40%", marginTop: 10 }}>SELECT 1</Text>
          </View>


        {/* Question 2 */}

          <View style={{ display: "flex", flexDirection: "row" }}>
            <View style={{ width: "50%" }}>
              <RadioButton
                value={true}
                status={
                  this.state.isSelected2 === true ? "checked" : "unchecked"
                }
                onPress={() => {
                  var isSelected2 = this.state.isSelected2;
                  const defaultPoints = this.state.totalPoints;
                  var totalPoints = defaultPoints + 2;
                  this.setState({
                    isSelected1: false,
                    isSelected2: !isSelected2,
                    isSelected3: false,
                    isSelected4: false,
                  });
                  !this.state.isSelected2 === true
                    ? this.setState({ totalPoints: totalPoints })
                    : this.setState({ totalPoints: defaultPoints });
                }}
              />
            </View>
            <Text style={{ marginLeft: "-40%", marginTop: 10 }}>SELECT 2</Text>
          </View>

        {/* Question 3 */}

          <View style={{ display: "flex", flexDirection: "row" }}>
            <View style={{ width: "50%" }}>
              <RadioButton
                value={true}
                status={
                  this.state.isSelected3 === true ? "checked" : "unchecked"
                }
                onPress={() => {
                  var isSelected3 = this.state.isSelected3;
                  const defaultPoints = this.state.totalPoints;
                  var totalPoints = defaultPoints + 3;
                  this.setState({
                    isSelected1: false,
                    isSelected2: false,
                    isSelected3: !isSelected3,
                    isSelected4: false,
                  });
                  !this.state.isSelected3 === true
                    ? this.setState({ totalPoints: totalPoints })
                    : this.setState({ totalPoints: defaultPoints });
                }}
              />
            </View>
            <Text style={{ marginLeft: "-40%", marginTop: 10 }}>SELECT 3</Text>
          </View>



        {/* Question 4 */}


          <View style={{ display: "flex", flexDirection: "row" }}>
            <View style={{ width: "50%" }}>
              <RadioButton
                value={true}
                status={
                  this.state.isSelected4 === true ? "checked" : "unchecked"
                }
                onPress={() => {
                  var isSelected4 = this.state.isSelected4;
                  const defaultPoints = this.state.totalPoints;
                  var totalPoints = defaultPoints + 4;
                  this.setState({
                    isSelected1: false,
                    isSelected2: false,
                    isSelected3: false,
                    isSelected4: !isSelected4,
                  });
                  !this.state.isSelected4 === true
                    ? this.setState({ totalPoints: totalPoints })
                    : this.setState({ totalPoints: defaultPoints });
                }}
              />
            </View>
            <Text style={{ marginLeft: "-40%", marginTop: 10 }}>SELECT 4</Text>
          </View>
        </View>
      </View>
    );
  }
}
const styles = StyleSheet.create({});
