import * as React from "react";
import {
  Text,
  View,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  FlatList,
  CheckBox,
} from "react-native";
import { RadioButton } from "react-native-paper";
import { Header, Icon } from "react-native-elements";

export default class CheckBoxScreen extends React.Component {
  constructor() {
    super();
    this.state = {
      isSelected1: false,
      isSelected2: false,
      isSelected3: false,
      isSelected4: false,
      totalPoints: 0,
    };
  }
  render() {
    console.log(this.state.isSelected1)
    return (
      <View>
        <Header
          leftComponent={
            <Icon
              name="arrow-left"
              type="feather"
              color="white"
              onPress={() => {
                this.props.navigation.navigate("Drawer");
              }}
            />
          }
          centerComponent={{
            text: "CheckBox",
            style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
          }}
          backgroundColor="black"
        />
        <View>
          <TouchableOpacity >
            <View style={{ display: "flex", flexDirection: "row" }}>
              <View style={{ width: "50%" }}>
                <RadioButton
                  value="first"
                  status={
                    this.state.isSelected1 === "first" ? "checked" : "unchecked"
                  }
                  onPress={() => this.setState({ isSelected1: "first" })}
                />
              </View>
              <Text style={{ marginLeft: "-40%", marginTop: 10 }}>
                SELECT 1
              </Text>
            </View>
          </TouchableOpacity>

          <RadioButton
            value="second"
            status={
              this.state.isSelected1 === "second" ? "checked" : "unchecked"
            }
            onPress={() => this.setState({ isSelected1: "second" })}
          />
          <Text>SELECT 2</Text>
        </View>
      </View>
    );
  }
}
const styles = StyleSheet.create({});
